function addCopyTextButton(){jQuery(".copytext").not(".initialized").each(function(){appendCopyTextToElement(this);jQuery(this).next().click(function(){var a=jQuery(this).prev().html();var d=a.replace(/<br>/g,"\n").replace(/<BR>/g,"\n").replace(/<li>/g,"").replace(/<LI>/g,"").replace(/<\/li>/g,"\n").replace(/<\/LI>/g,"\n");var c=window.navigator.userAgent;var b=c.indexOf("MSIE ");if(b>0||!!navigator.userAgent.match(/Trident.*rv\:11\./)){window.clipboardData.setData("Text",d)}else{window.prompt("Zkopírovat do schránky: Ctrl+C , Enter",d)}})})}function appendCopyTextToElement(b){jQuery(b).addClass("initialized");var a=jQuery("<input/>",{type:"button",value:"Zkopírovat",className:"copy-button noprint",title:"Vložit autorem připravenou část textu do schránky"});jQuery(b).after(a)};